package techelliptica;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String day = "MONDAY";

		switch (day) {
		case "Monday":
			System.out.println("Monday");
		case "MOnday":
			System.out.println("day 1");
			break;
		default:
			System.out.println("day not found");

		}

	}

}
